  }

  /* Mobile Portrait (below 768px) */
  @media (max-width: 767px) {
    .sidebar-desktop {
      display: none;
    }

    .header-mobile {
      padding: 0.75rem 1rem;
    }

    .header-title {
      font-size: 1.125rem;
    }

    .header-logo {
      width: 1.5rem;
      height: 1.5rem;
    }

    /* Full-width mobile layout */
    .main-content {
      width: 100%;
      padding: 0;
    }

    /* Stack editor and error panel vertically */
    .editor-container {
      flex-direction: column;
      height: 100%;
      min-height: 500px;
    }

    .code-editor-wrapper {
      min-height: 400px;
      flex: 1;
      display: flex;
      flex-direction: column;
    }

    .error-panel-mobile {
      width: 100%;
      max-height: 40vh;
      min-height: 200px;
      overflow-y: auto;
      border-left: none;
      border-top: 1px solid hsl(var(--border));
    }

    /* Horizontal scrolling tabs */
    .tab-navigation {
      padding: 0.5rem;
      overflow-x: auto;
      scrollbar-width: none;
      -ms-overflow-style: none;
    }

    .tab-navigation::-webkit-scrollbar {
      display: none;
    }

    .tab-container {
      display: flex;
      gap: 0.5rem;
      min-width: max-content;
      padding-bottom: 0.5rem;
    }

    .tab-button {
      padding: 0.75rem 1.25rem;
      font-size: 0.875rem;
      white-space: nowrap;
      flex-shrink: 0;
      min-width: max-content;
    }

    /* Full-width mobile sidebar */
    .sidebar-mobile {
      width: 100vw;
      max-width: 22rem;
    }

    /* Compact floating action button */
    .fab-mobile {
      bottom: 1rem;
      right: 1rem;
      width: 3.5rem;
      height: 3.5rem;
    }

    /* Mobile code editor adjustments */
    .monaco-editor {
      font-size: 12px !important;
      line-height: 18px !important;
    }

    /* Touch-friendly controls */
    .mobile-touch-friendly {
      min-height: 44px;
      min-width: 44px;
    }

    /* Hide non-essential elements on mobile */
    .mobile-hidden {
      display: none !important;
    }

    /* Compact sidebar sections */
    .sidebar-section {
      padding: 1rem;
    }

    .sidebar-section h3 {
      font-size: 0.875rem;
      margin-bottom: 0.75rem;
    }

    /* Mobile form adjustments */
    .mobile-form-group {
      margin-bottom: 1rem;
    }

    .mobile-select {
      font-size: 16px; /* Prevents zoom on iOS */
      min-height: 44px;
    }

    /* Mobile error cards */
    .error-card-mobile {
      padding: 1rem;
      margin-bottom: 0.75rem;
    }

    .error-card-mobile .error-title {
      font-size: 0.875rem;
      line-height: 1.3;
    }

    .error-card-mobile .error-description {
      font-size: 0.75rem;
      line-height: 1.4;
      margin-top: 0.5rem;
    }
  }

  /* Small Mobile (320px - 479px) */
  @media (max-width: 479px) {
    .header-mobile {
      padding: 0.5rem 0.75rem;
    }

    .header-title {
      font-size: 1rem;
    }

    .header-logo {
      width: 1.25rem;
      height: 1.25rem;
    }

    .sidebar-mobile {
      width: 100vw;
    }

    .tab-button {
      padding: 0.5rem 1rem;
      font-size: 0.75rem;
    }

    /* Extra compact Monaco editor */
    .monaco-editor {
      font-size: 11px !important;
      line-height: 16px !important;
    }

    /* Compact controls */
    .button-sm-mobile {
      padding: 0.375rem 0.75rem;
      font-size: 0.75rem;
      min-height: 40px;
    }

    /* Smaller error cards for tiny screens */
    .error-card-mobile {
      padding: 0.75rem;
      margin-bottom: 0.5rem;
    }

    .sidebar-section {
      padding: 0.75rem;
    }

    /* Smaller floating action button */
    .fab-mobile {
      width: 3rem;
      height: 3rem;
      bottom: 0.75rem;
      right: 0.75rem;
    }
  }

  /* Extra Small Mobile (below 320px) */
  @media (max-width: 319px) {
    .header-mobile {
      padding: 0.5rem;
    }

    .header-title {
      font-size: 0.875rem;
    }

    .tab-button {
      padding: 0.5rem 0.75rem;
      font-size: 0.625rem;
    }

    .monaco-editor {
      font-size: 10px !important;
      line-height: 14px !important;
    }

    .error-card-mobile {
      padding: 0.5rem;
    }

    .sidebar-section {
      padding: 0.5rem;
    }
  }

  /* Dark mode specific responsive adjustments */
  @media (prefers-color-scheme: dark) {
    .sidebar-mobile-overlay {
      background: rgba(0, 0, 0, 0.7);
    }
  }

  /* High DPI displays */
  @media (min-resolution: 2dppx) {
    .monaco-editor {
      font-feature-settings: "liga" 0;
    }
  }

  /* Landscape orientation optimizations */
  @media (orientation: landscape) and (max-height: 600px) {
    .header-mobile {
      padding: 0.5rem 1rem;
    }

    .sidebar-section {
      padding: 0.5rem;
    }
  }

  /* Desktop sidebar styling */
  .sidebar-desktop {
    width: 20rem;
    background: hsl(var(--background-elevated));
    border-right: 1px solid hsl(var(--border));
    overflow-y: auto;
  }

  /* Mobile sidebar improvements */
  .sidebar-mobile {
    background: hsl(var(--background-elevated));
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }

  /* Error panel mobile specific styling */
  .error-panel-mobile {
    min-height: 200px;
    max-height: 50vh;
  }

  /* Enhanced touch targets for mobile */
  @media (max-width: 768px) {
    button, [role="button"], select, input {
      min-height: 44px;
      min-width: 44px;
    }

    /* Improve select dropdown on mobile */
    select {
      font-size: 16px; /* Prevents zoom on iOS */
    }

    /* Better focus indicators for mobile */
    button:focus-visible,
    [role="button"]:focus-visible {
      outline: 2px solid hsl(var(--primary));
      outline-offset: 2px;
    }
  }

  /* Floating action button enhancements */
  .fab-mobile {
    position: fixed;
    z-index: 40;
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.3);
  }

  /* Monaco Editor mobile optimizations */
  @media (max-width: 768px) {
    .monaco-editor .editor-scrollable {
      overflow-x: auto !important;
    }

    .monaco-editor .view-lines {
      white-space: nowrap !important;
    }

    /* Better line numbers on mobile */
    .monaco-editor .margin-view-overlays .line-numbers {
      font-size: 10px !important;
    }
  }

  /* Responsive grid for error items */
  .error-grid-mobile {
    display: grid;
    gap: 0.75rem;
    grid-template-columns: 1fr;
  }

  @media (min-width: 640px) {
    .error-grid-mobile {
      grid-template-columns: repeat(2, 1fr);
    }
  }

  /* Accessibility improvements */
  @media (prefers-reduced-motion: reduce) {
    .sidebar-mobile {
      transition: none;
    }

    .animate-pulse-glow {
      animation: none;
    }
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .border-dark-border {
      border-color: hsl(var(--foreground)) !important;
    }

    .bg-dark-elevated {
      border: 1px solid hsl(var(--foreground));
    }
  }
}

