import * as monaco from 'monaco-editor';

// Simple worker configuration that doesn't cause issues
if (typeof self !== 'undefined') {
  self.MonacoEnvironment = {
    getWorker: function () {
      // Use a simple fallback that doesn't require worker modules
      return {
        postMessage: () => {},
        addEventListener: () => {},
