    total REAL4 ?
    hundred REAL4 100.0
    discount REAL4 0.9

.code
start:
    finit                   ; initialize FPU
    fldz                   ; load 0.0

    mov esi, OFFSET prices

sum_loop:
    fld REAL4 PTR [esi]    ; load price
    fcomp hundred          ; compare with 0
    fstsw ax
    sahf
    jz done

    fadd                   ; add to total
    add esi, 4
    jmp sum_loop

done:
